cd "${instance-zowe_install_path}/bin"
# global config
export ROOT_DIR=${instance-zowe_install_path}
export ZOWE_PREFIX=${instance-ZOWE_PREFIX}
export ZOWE_INSTANCE=${instance-ZOWE_INSTANCE}

# Comma separated list of components should start from [GATEWAY,DESKTOP]
export LAUNCH_COMPONENT_GROUPS=${instance-LAUNCH_COMPONENT_GROUPS}

export JAVA_HOME=${instance-JAVA_HOME}
export NODE_HOME=${instance-NODE_HOME}

# z/OS MF config
