export ZOSMF_HOST=${instance-ZOSMF_HOST}

export ZOWE_EXPLORER_HOST=${instance-ZOWE_EXPLORER_HOST}
export ZOWE_IP_ADDRESS=${instance-ZOWE_IP_ADDRESS}

# keystore config
export KEYSTORE_DIRECTORY=${instance-KEYSTORE_DIRECTORY}

# APIML variables
export CATALOG_PORT=${instance-CATALOG_PORT}
export DISCOVERY_PORT=${instance-DISCOVERY_PORT}
export GATEWAY_PORT=${instance-GATEWAY_PORT}
export APIML_ALLOW_ENCODED_SLASHES=${instance-APIML_ALLOW_ENCODED_SLASHES}
export APIML_CORS_ENABLED=${instance-APIML_CORS_ENABLED}
# this configuration is deprecated. Zowe start script will ignore this value and always set it to false
export APIML_PREFER_IP_ADDRESS=false
export APIML_GATEWAY_TIMEOUT_MILLIS=${instance-APIML_GATEWAY_TIMEOUT_MILLIS}
export APIML_SECURITY_X509_ENABLED=${instance-APIML_SECURITY_X509_ENABLED}
export APIML_SECURITY_ZOSMF_APPLID=${instance-APIML_SECURITY_ZOSMF_APPLID}
export APIML_SECURITY_AUTH_PROVIDER=${instance-APIML_SECURITY_AUTH_PROVIDER}
# List of discovery service URLs separated by comma
export ZWE_DISCOVERY_SERVICES_LIST=https://${instance-ZOWE_EXPLORER_HOST}:${instance-DISCOVERY_PORT}/eureka/
# Enable debug logging for Api Mediation Layer services
export APIML_DEBUG_MODE_ENABLED=${instance-APIML_DEBUG_MODE_ENABLED}
export APIML_MAX_CONNECTIONS_PER_ROUTE=${instance-APIML_MAX_CONNECTIONS_PER_ROUTE}
export APIML_MAX_TOTAL_CONNECTIONS=${instance-APIML_MAX_TOTAL_CONNECTIONS}
export APIML_GATEWAY_EXTERNAL_MAPPER=https://${instance-ZOWE_EXPLORER_HOST}:${instance-GATEWAY_PORT}/zss/api/v1/certificate/x509/map
export APIML_SECURITY_AUTHORIZATION_ENDPOINT_URL=https://${instance-ZOWE_EXPLORER_HOST}:${instance-GATEWAY_PORT}/zss/api/v1/saf-auth

####################
# caching service
# TCP port of caching service
export ZWE_CACHING_SERVICE_PORT=${instance-ZWE_CACHING_SERVICE_PORT}
# specify amount of records before eviction strategies start evicting
export ZWE_CACHING_STORAGE_SIZE=${instance-ZWE_CACHING_STORAGE_SIZE}
# specify eviction strategy to be used when the storage size is achieved
export ZWE_CACHING_EVICTION_STRATEGY=${instance-ZWE_CACHING_EVICTION_STRATEGY}
# specify persistent method of caching service
# possible options: <empty> (will fall back to inMemory), VSAM, redis
export ZWE_CACHING_SERVICE_PERSISTENT=${instance-ZWE_CACHING_SERVICE_PERSISTENT}
# specify the data set name of the caching service VSAM data set
export ZWE_CACHING_SERVICE_VSAM_DATASET=${instance-ZWE_CACHING_SERVICE_VSAM_DATASET}

# explorer variables
export JOBS_API_PORT=${instance-JOBS_API_PORT}
export FILES_API_PORT=${instance-FILES_API_PORT}

# app framework (app-server, zss, zis) variables
export ZWED_SERVER_HTTPS_PORT=${instance-ZWED_SERVER_HTTPS_PORT}
export ZWES_SERVER_PORT=${instance-ZWES_SERVER_PORT}
ZWES_SERVER_TLS=${instance-ZWES_SERVER_TLS}
export ZWES_XMEM_SERVER_NAME=${instance-ZWES_XMEM_SERVER_NAME}
export ZWES_ZIS_LOADLIB=${instance-ZWES_ZIS_LOADLIB}
export ZWES_ZIS_PLUGINLIB=${instance-ZWES_ZIS_PLUGINLIB}
export ZWES_ZIS_PARMLIB=${instance-ZWES_ZIS_PARMLIB}
export ZWES_ZIS_PARMLIB_MEMBER=${instance-ZWES_ZIS_PARMLIB_MEMBER}

# terminal app variables
export ZWED_SSH_PORT=${instance-ZWED_SSH_PORT}
export ZWED_TN3270_PORT=${instance-ZWED_TN3270_PORT}
export ZWED_TN3270_SECURITY=${instance-ZWED_TN3270_SECURITY}

# Log level for launch scripts. Set this to "debug" to see detail of validate and configure steps.
export ZWE_LOG_LEVEL_ZWELS=${instance-ZWE_LOG_LEVEL_ZWELS}

# Extender variables
export ZWEAD_EXTERNAL_STATIC_DEF_DIRECTORIES=${instance-ZWEAD_EXTERNAL_STATIC_DEF_DIRECTORIES}
export EXTERNAL_COMPONENTS=${instance-EXTERNAL_COMPONENTS}

sh ./zowe-configure-instance.sh -c "${instance-zowe_instance_path}"

